import React, { Component } from 'react'

export class ClassCounter extends Component {

  constructor(props) {
    super(props)

    this.state = {
       count: 0
    }
  }

  clickCounter = () => {
    this.setState({
        count: this.state + 1 
    })
  }

  render() {
    const {count} = this.state
    return (
      <div>
        <button onClick={this.clickCounter()}> click {count}</button>
      </div>
    )
  }
}

export default ClassCounter
